import React,{ useState} from "react";
import './Weather.css'

const Weather = () =>{

    const[data,setData] = useState({})

    const[location,setLocation]=useState('')
    const api = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=01cb49ff757bbed56f1ed4a169e056b1`
    // useEffect(()=>{

    // },[])

    function changehandler(event){
        setLocation(event.target.value)

    }

    const searchLocation = (event) =>{
        if(event.key === 'Enter'){
        fetch(api)
        .then(response=> { return response.json()})
        .then(wdata=>{console.log(wdata)
        setData(wdata)
        })

        }
    }
    return(
        <div className="app">
        <div className="search">
        <input 
        type="text"
        value={location}
        onChange={changehandler}
        onKeyPress={searchLocation}
        placeholder="Enter Location"
        />
         </div>
            <div className="Container">
                <div className="top">
                    <div className="location">
                        <p className="loc-font">Dallas</p>
                    </div>
                    <div className="temp">
                        <h1>{data.main.temp}°F</h1>
                    </div>
                    <div className="desc">
                        <p className="desc-font">Cloudy</p>
                    </div>
                <div className="buttom">
                    <div className="feels">
                        <p className="bold">65°F</p>
                        <p> Feels Like</p>
                    </div>
                    <div className="humidity">
                        <p className="bold">20%</p>
                        <p>Humidity</p>
                    </div>
                    <div className="wind">
                        <p className="bold">10MPH</p>
                        <p>Wind</p>
                    </div>
                </div>    
                </div>
            </div>

        </div>
    )
}

export default Weather
