import React from "react";

class Add extends React.Component{
    constructor(){
        super()
        this.state = {
            number : 0,
        }
    }

    increase =()=> {
        this.setState({number : this.state.number + 1})
    }

    decrease = () =>{
        this.setState({number : this.state.number - 1 })
    }



    render(){
        return(
            <div>
                <h1>QUANTITY : {this.state.number}</h1>
                <button onClick={this.increase}>+</button>
                <button>-</button>
            </div>
        )
    }
}

export default Add
